import { Callable, PinionContext } from '../core.js';
import type { Command } from 'commander';
/**
 * Parse the command line arguments using a commander instance and
 * add the parsed options to the context.
 * @param program The commander program instance
 * @returns
 */
export declare const commander: <C extends PinionContext>(program: Callable<Command, C>) => (ctx: C) => Promise<C & import("commander").OptionValues>;
